import React from "react";
import "./Searchbar.css";

const Searchbar = ({ getSetValue }) => {
  let getInput = null;
  const showInput = () => {
    getSetValue(getInput);
  };
  const inputNotNull = (e) => {
    document.getElementsByClassName("clear-icon").visibility = true;
  };
  return (
    <div className="wrapper">
      <img
        className="search-icon"
        src={require("../../../assets/img/mag.png")}
        alt="mag-glass"
        onClick={showInput}
      />
      <input
        className="search"
        placeholder="Search..."
        type="text"
        onChange={(e) => inputNotNull()}
      />
      <img
        className="clear-icon"
        src={require("../../../assets/img/cross.png")}
        alt="cross"
      />
    </div>
  );
};
export default Searchbar;
