import React, { useState, useEffect } from "react";
import "./Home.css";
import Movie from "../../components/Movie/Movie";
import axios from "axios";
import Api from "../../api/Api";
import Searchbar from "../../components/Navbar/Searchbar/Searchbar";

const Home = () => {
  const [movies, setMovies] = useState([]);
  const [series, setSeries] = useState([]);
  let searchedSeries;
  const [searchValue, setSearchValue] = useState("");

  const setSearchState = (input) => {
    console.log("[Home.js] input: " + input);
    setSearchValue(input);
  };
  const findSearchedItem = () => {
    let foundItem;
    searchedSeries = series.find(
      (el) => el.title.toLowerCase() === searchValue.toLocaleLowerCase()
    );
    searchedSeries
      ? (foundItem = (
          <Movie
            key={searchedSeries.id}
            id={searchedSeries.id}
            title={searchedSeries.title}
            release={searchedSeries.release}
            description={searchedSeries.description}
            rating={searchedSeries.rating}
          />
        ))
      : (foundItem = (
          <h1 style={{ backgroundColor: "red" }}>AZ ELEM NEM TALÁLHATÓ!</h1>
        ));
    return foundItem;
  };
  useEffect(() => {
    axios
      .get(`${Api.url}/api/movies/`)
      .then((res) => {
        const getMovies = res.data;
        setMovies(getMovies);
      })
      .catch((err) => console.log("hiba: " + err));

    axios.get(`${Api.url}/api/tvseries/`).then((res) => {
      const getSeries = res.data;
      setSeries(getSeries);
    });
  }, []);

  return (
    <div className="home-container ">
      <Searchbar getInputSetter={setSearchState} />
      <h1 className="h1-mov">Movies</h1>
      <hr className="separator" />
      <div>
        {movies.map((el) => {
          return (
            <Movie
              key={el.id}
              id={el.id}
              title={el.title}
              release={el.release}
              description={el.description}
              rating={el.rating}
            />
          );
        })}
        <h1>Series</h1>
        <hr className="separator" />
        {searchValue
          ? findSearchedItem()
          : series.map((el) => {
              return (
                <Movie
                  key={el.id}
                  id={el.id}
                  title={el.title}
                  release={el.release}
                  description={el.description}
                  rating={el.rating}
                />
              );
            })}
      </div>
    </div>
  );
};
export default Home;
