import React from "react";
import "./Searchbar.css";


const Searchbar = ({ getSetValue }) => {
  let getInput;
  let visibleCross = "hidden";

  const showInput = () => {
    getSetValue(getInput);
  };
   const inputNotNull = () => {
    getInput ? visibleCross == "visible":null;
  };
  return (
    <div className="wrapper">
      <img
        className="search-icon"
        src={require("../../../assets/img/mag.png")}
        alt="mag-glass"
        onClick={showInput}
      />
      <input
        className="search"
        placeholder="Search..."
        type="text"
        onChange={(e) => console.log(getInput = e.target.value)}
      />
      <img
        className="clear-icon"
        src={require("../../../assets/img/cross.png")}
        alt="cross"
        style={{visibility:visibleCross}}
      />
    </div>
  );
};
export default Searchbar;
