import React from "react";
import {useState} from 'react'
import "./Searchbar.css";


const Searchbar = ({ getSetValue }) => {
  /* let getInput=null; */
  const [cross,setCross] =useState("hidden")

   const inputNotNull = (e) => {
     console.log('inputNotNull :'+e)
    getInput ? setCross("visible"):setCross("hidden");
  };
  return (
    <div className="wrapper">
      <img
        className="search-icon"
        src={require("../../../assets/img/mag.png")}
        alt="mag-glass"

      />
      <input
        className="search"
        placeholder="Search..."
        type="text"
        onChange={(e) => inputNotNull(e.target.value)}
      />
      <img
        className="clear-icon"
        src={require("../../../assets/img/cross.png")}
        alt="cross"
        style={{visibility:cross}}
      />
    </div>
  );
};
export default Searchbar;
