import React, { useState } from "react";
import "../home/Home.css";
import "./AboutUs.css";
import userIMG from "../../assets/img/user.png";
import yellowArrow from "../../assets/img/yellow_arrow.svg";
/* import translate from "../../i18n/messages/translate"; */

function AboutUs() {
  const [extendbio1, setExtendBio1] = useState(0);
  const [extendbio2, setExtendBio2] = useState(0);
  const [extendbio3, setExtendBio3] = useState(0);

  const name1 = "Németh András";
  const pos1 = " Front-End fejlesztő";
  const thoughts1 = "Ingyen dolgozok nem is tudom minek...";

  const name2 = "Zsíros Dominik";
  const pos2 = " Project manager";
  const thoughts2 = "Ez nem best practice. Csináljátok már!";

  const name3 = "Fási Milán";
  const pos3 = " Back-End fejlesztő";
  const thoughts3 = "A filmek jók kép nélkül is. Amúgy egy LoL?";

  const setAnimation = (idVal) => {
    if (idVal === 1) {
      setExtendBio1(extendbio1 ? 0 : 1);
    } else if (idVal === 2) {
      setExtendBio2(extendbio2 ? 0 : 1);
    } else {
      setExtendBio3(extendbio3 ? 0 : 1);
    }
  };
  return (
    <div className="home-container">
      <div className="bio-wrapper">
        <div className="bio-profile-wrapper">
          <div className="bio-img-wrapper">
            <img src={userIMG} className="bio-img" alt="userIMG" />
          </div>
          <div className="arrow-wrapper">
            <img
              src={yellowArrow}
              className="yellow-arrow"
              alt="arrow"
              onClick={() => setAnimation(1)}
            />
          </div>
          <div className="bio-description" extendbio={extendbio1} id="desc-1">
            <h1>
              Name:
              {name1}
            </h1>
            <h1>
              Position:
              {pos1}
            </h1>
            <h1>Thoughts about the project:</h1>
            <p>{thoughts1}</p>
          </div>
        </div>

        <div className="bio-profile-wrapper">
          <div className="bio-img-wrapper">
            <img src={userIMG} className="bio-img" alt="userIMG" />
          </div>
          <div className="arrow-wrapper">
            <img
              src={yellowArrow}
              className="yellow-arrow"
              alt="arrow"
              onClick={() => setAnimation(2)}
            />
          </div>
          <div className="bio-description" extendbio={extendbio2} id="2">
            <h1>
              Name:
              {name2}
            </h1>
            <h1>
              Position:
              {pos2}
            </h1>
            <h1>Thoughts about the project:</h1>
            <p>{thoughts2}</p>
          </div>
        </div>

        <div className="bio-profile-wrapper">
          <div className="bio-img-wrapper">
            <img src={userIMG} className="bio-img" alt="userIMG" />
          </div>
          <div className="arrow-wrapper">
            <img
              src={yellowArrow}
              className="yellow-arrow"
              alt="arrow"
              onClick={() => setAnimation(3)}
            />
          </div>
          <div className="bio-description" extendbio={extendbio3} id="3">
            <h1>
              Name:
              {name3}
            </h1>
            <h1>
              Position:
              {pos3}
            </h1>
            <h1>Thoughts about the project:</h1>
            <p>{thoughts3}</p>
          </div>
        </div>
      </div>
    </div>
  );
}
export default AboutUs;
